mapper.update:
UPDATE TRIO_BOARD
SET board_views = board_views + 1
WHERE board_code = #{boardCode}

UPDATE TRIO_BOARD
SET status_code = '20'
WHERE board_code = #{boardCode}

UPDATE TRIO_BOARD
SET category_code = #{categoryCode},
board_title = #{boardTitle}
WHERE board_code = #{boardCode}

UPDATE TRIO_BOARD_CONTENT
SET board_content = #{boardContent}
WHERE board_code = #{boardCode}
