mapper.select:
SELECT YEAR(o.order_date) AS order_year, SUM(oi.order_qty * i.item_price) AS total_sales FROM TRIO_ORDER_ITEMS oi JOIN TRIO_ORDERS o ON oi.order_code = o.order_code JOIN TRIO_ITEMS i ON oi.item_code = i.item_code WHERE o.status_code = '30'
AND YEAR(o.order_date) = #{year}
GROUP BY YEAR(o.order_date) ORDER BY order_year;

SELECT DATE_FORMAT(o.order_date, '%m') AS order_month, SUM(i.item_price * oi.order_qty) AS total_sales FROM TRIO_ORDER_ITEMS oi JOIN TRIO_ORDERS o ON oi.order_code = o.order_code JOIN TRIO_ITEMS i ON oi.item_code = i.item_code WHERE YEAR(o.order_date) = #{year} AND o.status_code = '30'
AND DATE_FORMAT(o.order_date, '%m') = #{month}
GROUP BY DATE_FORMAT(o.order_date, '%Y-%m') ORDER BY DATE_FORMAT(o.order_date, '%Y-%m')
